def process(item):

	if item['type'] == 'title':
		return title(item)
	if item['type'] == 'ul':
		return ul(item)
	if item['type'] == 'li':
		return li(item)
	if item['type'] =='text':
		return text(item)
	if item['type'] =='tag':
		return tag(item)
	if item['type'] =='miniul':
		return miniul(item)

	if item['type'] =='info':
		return info(item)

	if item['type'] =='meta':
		return meta(item)

	if item['type'] =='dates':
		return dates(item)

	if item['type'] =='date':
		return date(item)

	return "don't know "+ item['type']

def text(item):
	txt =  item['text']
	txt = re.sub(r"http[s]?://(?:[a-zA-Z]|[0-9]|[$-_@.&+~#]|[!*\(\),]|(?:%[0-9a-fA-F][0-9a-fA-F]))+([a-zA-Z]|[0-9]|\/)+",  '<a href=\"\g<0>\">\g<0></a>',txt)	
	return txt

def info(item):
	return item['text']

def meta(item):
	return "<p class=\"meta\">" + text(item) + "</p>"

def dates(item):

	if len(item['text']) == 1:
		return item['text'][0]['name'] + ": " + item['text'][0]['date'] + "<br>"
	else:
		txt = ""
		txt += "<table>"
		for y,x in enumerate(item['text']):
			if y %2 == 0:
				txt += "<tr>" + process(x) +  " </tr>"
			else:
				txt += "<tr class=\"odd\">" + process(x) +  " </tr>"
		txt += "</table>"
	return txt

def date(item):
	txt = ""
	txt += "<td>" + item['name'] + ": </td>"
	txt += "<td>" + item['date'] + "</td>"
	return txt


def miniul(item):
	txt = ""
	txt += "<ul>"
	for x in item['text']:
		txt += "<li>" + process(x) +  " </li>"
	txt += "</ul>"
	return txt

def ul(item):
	if item['type']!= 'ul':
		raise ValueError

	txt = ""
	txt += "<ul>"
	for x in item['text']:
		txt +=  process(x) 
	txt += "</ul>"
	return txt

def li(item):
	if item['type']!= 'li':
		raise ValueError

	txt = ""
	txt += "<li>"
	for x in item['text']:
		txt +=  process(x) 
		if x['type'] not in ['miniul','dates']:
			txt += "<br/>"
	txt += "</li>"
	return txt




def title(item):
	txt = "<h2>"
	if item['tag']:
		txt += "<a id=\"{}\" name=\"{}\" ></a>".format(item['tag'],item['tag'])

	txt += item['text']
	txt += "</h2>"
	return txt

